import React from 'react';


import { setToken} from "../AuthHelper";
import {BrowserRouter, Link, Route} from 'react-router-dom'
import axios from "axios";









const emailRegex = RegExp(/^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/);

const formValid = ({formErrors, ...rest }) => {
    let valid = true;

    Object.values(formErrors).forEach(val =>{
        val.length > 0 && (valid= false);
    });

    Object.values(rest).forEach(val => {
        val == null && (valid = false)
    })

    return valid;   
};  

class Signup extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            firstName: null,
            lastName: null,
            email: null,
            password: null,
            formErrors:{
                firstName:"",
                lastName:"",
                email:"",
                password:""
            }
        };
    }



    handleChange = e => {
        e.preventDefault();
        const {name, value} = e.target;
        let formErrors = this.state.formErrors;
        
        switch (name) {
            case 'firstName':
                formErrors.firstName = value.length < 3 ? "minimum 3 characters required" :  "";
                break;
            case 'lastName':
                formErrors.lastName = value.length < 3 ? "minimum 3 characters required" :  "";
                break;
            case 'email':
                formErrors.email = emailRegex.test(value) ? "" :  "invalid email adress";
                break;
            case 'password':
                formErrors.password = value.length < 6 ? "minimum 6 characters required" :  "";
                break;
                default:
                break;
                                                            
        }

        this.setState({formErrors, [name]: value }, () => console.log(this.state))
    }


    handleSumbit = e => {
      e.preventDefault();
        axios({
            method: 'post',
            url: 'http://localhost:3010/signup',
            data:{
                firstName: this.state.firstName,
                lastName: this.state.lastName,
                email: this.state.email,
                password: this.state.password
            }
        }).then((result) => {
            if (result && result.data && result.data.signedJWT) {
                setToken(result.data.signedJWT)
                this.props.history.replace('/');
            }
        })
    };



    render() {
        const { formErrors} = this.state;
        return (
            <div className="wrapper">
                <div className="form-wrapper">
                    <h1>Create Account</h1>
                    <form onSubmit={this.handleSumbit} noValidate>
                        <div className="firstName">
                            <label htmlFor="firstName">First Name</label>
                            <input  
                            className={formErrors.firstName.length > 0 ? "error" : null} 
                            placeholder="First Name" 
                            type="text" 
                            name="firstName" 
                            noValidate
                            onChange={this.handleChange}
                            />
                            {formErrors.firstName.length > 0 && (
                                <span className="errorMessage">{formErrors.firstName}</span>
                            )}
                        </div>
                        <div className="lastName">
                            <label htmlFor="lastName">Last Name</label>
                            <input 
                            className={formErrors.lastName.length > 0 ? "error" : null} 
                            placeholder="Last Name" 
                            type="text" 
                            name="lastName" 
                            noValidate
                            onChange={this.handleChange}
                            />
                            {formErrors.lastName.length > 0 && (
                                <span className="errorMessage">{formErrors.lastName}</span>
                            )}
                        </div>
                        <div className="email">
                            <label htmlFor="email">Email</label>
                            <input 
                            className={formErrors.email.length > 0 ? "error" : null} 
                            placeholder="Email" 
                            type="email" 
                            name="email" 
                            noValidate
                            onChange={this.handleChange}
                            /> 
                            {formErrors.email.length > 0 && (
                                <span className="errorMessage">{formErrors.email}</span>
                            )}
                        </div>
                        <div className="password">
                            <label htmlFor="password">Password</label>
                            <input 
                            className={formErrors.password.length > 0 ? "error" : null}  
                            placeholder="Password" 
                            type="password" 
                            name="password" 
                            noValidate
                            onChange={this.handleChange}
                            />
                            {formErrors.password.length > 0 && (
                                <span className="errorMessage">{formErrors.password}</span>
                            )}
                        </div>


                            <div className="createAccount">
                                <button type="submit"> Create Account</button>
                                <Link to="/login">Already Have an Account?</Link>
                            </div>

                    </form>
                    
                </div>
            </div>
        );
    }
}



export default Signup;
